mapper.insert:
        INSERT INTO product_dtl_lst
        (
        	IDX
        	, SHOPIDX
        	, PRODUCTIDX
        	, DTLNOMALAMT
        	, DTLSHIPAMT
        	, DTLCOMMITIONAMT
        	, DTLCALCAMT
        	, DTLPAYAMT
        	, DTLINCOMMAMT
        	, DTLLOWAMT
        	, DTLPRODUCTLINK
        	, USEYN
        	, CREATEDTM
        	, AUDITID
        	, AUDITDTM
        )
        VALUES
        (
        	IFNULL(#{IDX},
	        	(IFNULL(
	            	(SELECT 
	            		MAX(CAST(cl.IDX AS INTEGER))
	        		  FROM product_dtl_lst cl
	        		 WHERE 1 = 1
	        		) , 0
	        	) +1)
	        )
        	, #{SHOPIDX}
        	, #{PRODUCTIDX}
        	, #{DTLNOMALAMT}
        	, #{DTLSHIPAMT}
        	, #{DTLCOMMITIONAMT}
        	, #{DTLCALCAMT}
        	, #{DTLPAYAMT}
        	, #{DTLINCOMMAMT}
        	, #{DTLLOWAMT}
        	, #{DTLPRODUCTLINK}
        	, 'Y'
        	, CURRENT_TIMESTAMP(6)
        	, #{AUDITID}
        	, CURRENT_TIMESTAMP(6)
        )
        ON DUPLICATE KEY UPDATE
        	DTLNOMALAMT			= #{DTLNOMALAMT}
        	, DTLSHIPAMT		= #{DTLSHIPAMT}
        	, DTLCOMMITIONAMT	= #{DTLCOMMITIONAMT}
        	, DTLCALCAMT		= #{DTLCALCAMT}
        	, DTLPAYAMT			= #{DTLPAYAMT}
        	, DTLINCOMMAMT		= #{DTLINCOMMAMT}
        	, DTLLOWAMT			= #{DTLLOWAMT}
        	, DTLPRODUCTLINK	= #{DTLPRODUCTLINK}
        	, SHOPIDX			= #{SHOPIDX}
        	, AUDITID			= #{AUDITID}
        	, AUDITDTM			= CURRENT_TIMESTAMP(6)
