mapper.select:
SELECT pdl.IDX , pdl.SHOPIDX , pdl.PRODUCTIDX , pdl.DTLSHIPAMT , pdl.DTLCOMMITIONAMT , pdl.DTLCALCAMT , pdl.DTLPAYAMT , pdl.DTLINCOMMAMT , pdl.DTLLOWAMT , pdl.USEYN , pdl.CREATEDTM , pdl.AUDITID , pdl.AUDITDTM , pdl.DTLPRODUCTLINK , pdl.DTLNOMALAMT FROM ( SELECT pdl.IDX , pdl.SHOPIDX , pdl.PRODUCTIDX , pdl.DTLSHIPAMT , pdl.DTLCOMMITIONAMT , pdl.DTLCALCAMT , pdl.DTLPAYAMT , pdl.DTLINCOMMAMT , pdl.DTLLOWAMT , pdl.USEYN , pdl.CREATEDTM , pdl.AUDITID , pdl.AUDITDTM , pdl.DTLPRODUCTLINK , pdl.DTLNOMALAMT , if( IFNULL(CAST(pdl.DTLPAYAMT as unsigned INTEGER),0) = 0 , '9999999999999999999999999' , pdl.DTLPAYAMT) as orderNum FROM product_dtl_lst pdl WHERE 1 = 1 AND pdl.USEYN = 'Y'
AND pdl.SHOPIDX = #{SHOPIDX}
AND pdl.PRODUCTIDX = #{PRODUCTIDX}
) pdl order by CAST(pdl.orderNum as unsigned)

mapper.update:
UPDATE product_dtl_lst
SET
	DTLNOMALAMT			= #{DTLNOMALAMT}
	, DTLSHIPAMT		= #{DTLSHIPAMT}
	, DTLCOMMITIONAMT	= #{DTLCOMMITIONAMT}
	, DTLCALCAMT		= #{DTLCALCAMT}
	, DTLPAYAMT			= #{DTLPAYAMT}
	, DTLINCOMMAMT		= #{DTLINCOMMAMT}
	, DTLLOWAMT			= #{DTLLOWAMT}
	, DTLPRODUCTLINK	= #{DTLPRODUCTLINK}
	, SHOPIDX			= #{SHOPIDX}
	, AUDITID			= #{AUDITID}
	, AUDITDTM			= CURRENT_TIMESTAMP(6)
WHERE IDX				= #{IDX}
  AND SHOPIDX			= #{OLDSHOPIDX}
  AND PRODUCTIDX		= #{PRODUCTIDX}

        DELETE FROM product_dtl_lst
        WHERE IDX				= #{IDX}
		  
          AND SHOPIDX			= #{OLDSHOPIDX}
          
		  
          AND PRODUCTIDX		= #{PRODUCTIDX}
