mapper.insert:
INSERT INTO shop_lst
(
	IDX
	, CREATEDTM
	, AUDITDTM
	, AUDITID
	, SHOPNM
	, ID
	, PWD
	, TOKENID
	, URL
)
VALUES
(
	(IFNULL(
    	(SELECT 
    		MAX(CAST(sl.IDX AS INTEGER))
		  FROM shop_lst sl
		 WHERE 1 = 1
		) , 0
	) +1)
	, sysdate()
	, sysdate()
	, #{AUDITID}
	, #{SHOPNM}
	, #{ID}
	, #{PWD}
	, #{TOKENID}
	, #{URL}
)

mapper.update:
UPDATE shop_lst
SET
	AUDITDTM		= sysdate()
	, AUDITID		= #{AUDITID}
	, SHOPNM		= #{SHOPNM}
	, ID			= #{ID}
	, PWD			= #{PWD}
	, TOKENID		= #{TOKENID}
	, URL			= #{URL}
WHERE IDX			= #{IDX}
