mapper.insert:
INSERT INTO produc_amt_search_log
(
	IDX
	, PRODUCTIDX
	, ALAMMSG
	, AUDITDTM
	, AUDITID
	, ALAMYN
)
VALUES
(
	(IFNULL(
    	(SELECT 
    		MAX(CAST(psl.IDX AS INTEGER))
		  FROM produc_amt_search_log psl
		 WHERE 1 = 1
		) , 0
	) +1)
	, #{PRODUCTIDX}
	, #{ALAMMSG}
	, sysdate()
	, #{AUDITID}
	, #{ALAMYN}
)

mapper.update:
UPDATE produc_amt_search_log
SET
	PRODUCTIDX		= #{PRODUCTIDX}
	, ALAMMSG		= #{ALAMMSG}
	, AUDITDTM		= sysdate()
	, AUDITID		= #{AUDITID}
	, ALAMYN		= #{ALAMYN}
WHERE IDX			= #{IDX}
