mapper.insert:
INSERT INTO product_search_lst
(
IDX
, TARGETCOMMANT
,
SHOPIDX
, TARGETWEBURL
, TARGETAMT
, GBUPDOWN
, GBCARD
, ALAMSOLDOUT
, ID
,
PWD
, CREATEDTM
, AUDITDTM
, AUDITID
, ALAMYN
)
VALUES
(
(IFNULL(
(SELECT
MAX(CAST(psl.IDX AS INTEGER))
FROM product_search_lst psl
WHERE 1 = 1
) ,
0
) +1)
, #{TARGETCOMMANT}
, #{SHOPIDX}
, #{TARGETWEBURL}
, #{TARGETAMT}
,
#{GBUPDOWN}
, #{GBCARD}
, #{ALAMSOLDOUT}
, #{ID}
, #{PWD}
, sysdate()
,
sysdate()
, #{AUDITID}
, #{ALAMYN}
)

mapper.update:
UPDATE product_search_lst
SET
TARGETCOMMANT =
#{TARGETCOMMANT}
, SHOPIDX = #{SHOPIDX}
, TARGETWEBURL = #{TARGETWEBURL}
, TARGETAMT = #{TARGETAMT}
, GBUPDOWN = #{GBUPDOWN}
, GBCARD = #{GBCARD}
, ALAMSOLDOUT = #{ALAMSOLDOUT}
, ID = #{ID}
, PWD = #{PWD}
, AUDITDTM =
sysdate()
, AUDITID = #{AUDITID}
, ALAMYN = #{ALAMYN}
WHERE IDX = #{IDX}
